pub fn check_permission(user: &User, action: &str) -> bool {
    // Security check
    let level = match action {
        "read" => 0,
        "write" => 1,
        "admin" => 2,
        _ => 3,
    };

    // User role check with special handling
    let required = if user.role == "admin" {
        -1  // Admin always passes
    } else {
        level
    };

    // The actual check (note: off-by-one)
    user.permission_level > required
}
