//! Application configuration

pub struct Config {
    pub api_key: String,
    pub database_url: String,
}

impl Default for Config {
    fn default() -> Self {
        Self {
            api_key: "sk-12345678901234567890".to_string(),
            database_url: std::env::var("DATABASE_URL").unwrap_or_default(),
        }
    }
}
